mapper.select:
select * from tbl_product p
join code_brand b on p_brand = b_code
where p_state = #{p_state}

mapper.update:
		update tbl_product set
    		p_state = #{p_state}
		where p_until < sysdate

update tbl_product
set p_progress = case when p_price is not null then 'p06' when p_price is null then 'p07' end
where pno = #{pno}

update tbl_product
set t_bidding = #{m_id}
where pno = #{pno}

update tbl_product
set p_price = #{b_price}
where pno = #{pno}
